sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	$scope.showOrder = function(num){
		c.data.answer=num;
		console.log('showOrder():num='+num);
		c.server.update();
	}

};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	//running from c.server.update()
	if(input){
		console.log("serverScriptAnswer: "+input.answer);
	}


	data.userName = gs.getUserDisplayName();
	data.table={
		"list":[]
	};

	var GR = new GlideRecord('incident');
	GR.addEncodedQuery('active=true^category=software^stateIN2,3');
	GR.orderBy('number');
	GR.query();
	while(GR.next()){
		var obj = {}
		obj.name = GR.getDisplayValue('caller_id');
		obj.order= GR.getValue('number');
		data.table.list.push(obj);
	}
	//gs.info(JSON.stringify(data.table.list));
})();
